function imagegen(prompt) {
        return new Promise((ngik, ngok) => {
            axios.get('https://image.tamsisxcode.repl.co/generate/'+prompt.replace(' ','%20'))
                .then(gen => {
                    if (gen.data.result) {
                        ngik(gen.data.result);
                    } else {
                        ngok(gen.data);
                    }
                })
        });
    }

